        jTextArea4.setFont(new java.awt.Font("Dialog", 1, 12)); // NOI18N
        jTextArea4.setForeground(new java.awt.Color(255, 255, 255));
        jTextArea4.setLineWrap(true);
        jTextArea4.setRows(5);
        jTextArea4.setText("Los tulipanes, además de embellecer jardines, también ofrecen ciertos beneficios para el suelo. \n\nAl ser plantas bulbosas, sus raíces ayudan a mejorar la estructura del suelo al favorecer una mayor aireación y drenaje, lo que es particularmente útil en suelos que tienden a retener demasiada humedad.");
        jTextArea4.setWrapStyleWord(true);
        jScrollPane4.setViewportView(jTextArea4);

        jTextArea5.setEditable(false);
        jTextArea5.setBackground(new java.awt.Color(0, 204, 102));
